import React, { useState } from "react";
import ReactDOM from "react-dom";

import DeckList from "./components/DeckList";
import SearchBar from "./components/SearchBar";
import SearchResults from "./components/SearchResults";

import { fetchCards } from "./api";

import "./index.css";

import axios from "axios";

// A component is a JS function that returns JSX (or HTML)
// Good practice is to make component start with an uppercase letter
// The JSX returned by your component has to have one parent / root element
const App = () => {
  console.log("rendering APP");

  const [results, setResults] = useState([]);
  const [deck, setDeck] = useState([]);

  // we NEVER can directly update state
  function addCardToDeck({ cardId, name }) {
    console.log("adding card to deck...");
    console.log(cardId);
    // const deckCopy = deck; <- FAKE copy

    const newDeck = [...deck]; // <- REAL copy
    // is this card already in our deck?
    const index = newDeck.findIndex((card) => card.id === cardId);
    // if so, just update the count of the existing object
    if (index > -1) {
      newDeck[index].count = newDeck[index].count + 1;
    } else {
      newDeck.push({ id: cardId, name: name, count: 1 });
    }
    setDeck(newDeck);
  }

  const removeCardFromDeck = ({ id }) => {
    const nextDeck = [...deck];
    // let arr = [ {id: 90834920, name: 'bananas'}, {id: 384902, name: 'apples'} ];
    // arr[1].name = 'mangos';
    const index = nextDeck.findIndex((card) => card.id === id);

    if (index === -1) {
      // don't do anything if we're trying to remove a card not in the deck
      return;
    }

    if (nextDeck[index].count === 1) {
      // remove the card altogether
      nextDeck.splice(index, 1);
    } else {
      // decrement the count
      nextDeck[index].count -= 1;
    }

    setDeck(nextDeck);
  };

  return (
    <div id="app">
      <SearchBar setResults={setResults} />
      <SearchResults
        results={results}
        removeCardFromDeck={removeCardFromDeck}
        addCardToDeck={addCardToDeck}
      />
      <DeckList
        deck={deck}
        removeCardFromDeck={removeCardFromDeck}
        addCardToDeck={addCardToDeck}
      />
    </div>
  );
};

ReactDOM.render(<App />, document.getElementById("root"));

