//-------------------------------------------------------------------------------------------------------------------------------------------------------------

import React, { useState } from "react";

import { fetchCards } from "../api";

const SearchBar = (props) => {
  console.log("rendering SearchBar");
  const [name, setName] = useState("");
  const [text, setText] = useState("");

  function handleName(event) {
    console.log("Name is changing...");
    console.log(event.target.value);
    setName(event.target.value);
  }

  function handleText(event) {
    setText(event.target.value);
  }

  async function searchCards(event) {
    console.log("CLICKED ON SEARCH");
    // prevent Default
    event.preventDefault();
    // grab ahold of text inside of input (What is my user searching for?)

    // call my api (fetchCards) and pass in the user form input
    const newResults = await fetchCards({ name, text });
    // call setResults so that other components know about this change of state
    props.setResults(newResults);
  }

  return (
    <div id="search">
      <h3>Look up cards here...</h3>
      <form>
        <input
          type="text"
          placeholder="name"
          id="card"
          value={name}
          onChange={handleName}
        />
        <input
          type="text"
          placeholder="card text"
          value={text}
          id="card"
          onChange={handleText}
        />
        <button type="submit" onClick={searchCards}>
          Search
        </button>
      </form>
    </div>
  );
};

export default SearchBar;
