// export default DeckList;

//-----------------------------------------------

import React from "react";

const DeckList = (props) => {
  console.log("rendering DeckList", props.deck);

  // reduce goes through array and produces a running sum of (in this case) total card count
  let cardCount = props.deck.reduce((cardCount, card) => {
    return cardCount + card.count;
  }, 0);

  return (
    <div id="deck">
      <h3>Your deck so far {cardCount}:</h3>
      {props.deck.map((card, idx) => {
        return (
          <p key={card.id}>
            {card.name} ({card.count})
          </p>
        );
      })}
    </div>
  );
};

export default DeckList;
